<!DOCTYPE html><html><head>    <script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.3/jquery.min.js"></script></head><body><h2>AJAX</h2><button type="button" onclick="loadDoc()">Request data</button><p id="demo"></p></body><script type="text/javascript">    var email = "test@test.com";    var password = "test";    $.ajax({        type: "POST",        url: "http://bogdanstanga.com/hacktm/v1/login",        data: {email:email, password:password},        dataType: "json",        success: function (data) {            if(data.error == false){                var user = data.user;                var name = user.name                var api = user.api_key;                alert("Name: " + name + " Api: " + api)            }else{                alert("Error");            }        },        error : function(e, status_code){            alert(e + " " + status_code);        }    });</script></html>